mapper.select:
       select  bno,title,summary,content,regDate,cnt,thumbnail
        from
      (
  		 select /*+index_desc(notice pk_notice) */ 
  		           rownum rn, bno,title,summary,content,regDate,cnt,thumbnail
  		   from notice where



		rownum<=#{pageNum}*#{amount}
		)
		where rn > (#{pageNum}-1)*#{amount}

mapper.update:
		update notice set 
									title=#{title},
									summary=#{summary},
             						content=#{content},
             						thumbnail=#{thumbnail}
  	 where bno=#{bno}
